import "./styles/Card.css";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { addFav, removeFav } from "../redux/actions";
import { useState } from "react";

function Card(
  props,
  {
    id,
    name,
    status,
    species,
    gender,
    origin,
    image,
    onClose,
    addFav,
    removeFav,
  }
) {
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      removeFav(id);
    } else {
      setIsFav(true);
      addFav(props);
    }
  };

  return (
    <div className="card">
      <button className="closeButton" onClick={() => onClose(id)}>
        ‚ùå
      </button>
      {isFav ? (
        <button onClick={handleFavorite}>‚ù§Ô∏è</button>
      ) : (
        <button onClick={handleFavorite}>ü§ç</button>
      )}

      <Link to={`/detail/${id}`} className="stylename">
        <h2 className="stylename">{name}</h2>
        ‚¨áÔ∏è
      </Link>
      <h2> {status}</h2>
      <h2 className="h2gender">{species}</h2>
      <h2>{gender}</h2>
      <h2>{origin}</h2>
      <img src={image} alt={name} />
    </div>
  );
}
const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (ch) => {
      dispatch(addFav(ch));
    },

    removeFav: (id) => {
      dispatch(removeFav(id));
    },
  };
};

const mapStateToProps = (dispatch) => {
  return {
    myFavorites: dispatch.myFavorites,
  };
};
export default connect(null, mapStateToProps, mapDispatchToProps)(Card);
