import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./styles/Login.css";

const regexEmail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/;

const regexpPassword =
  /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{8,15}/;

export default function Login() {
  const [inputs, Setinputs] = useState({
    email: "k",
    password: "k",
  });

  const [errors, Seterrors] = useState({
    email: "",
    password: "",
  });

  function validate(inputs) {
    const errors = {};

    if (!inputs.email) {
      errors.email = "El campo email es obligatorio.";
    } else if (inputs.email.length > 35) {
      errors.email = "No puede superar los 35 caracteres";
    } else if (!regexEmail.test(inputs.email)) {
      errors.email = "Email invalido";
    } else if (!inputs.password) {
      errors.password = "Debe haber un password";
    } else if (!regexpPassword.test(inputs.password)) {
      errors.password = "Debe ser un password valido";
    }

    return errors;
  }

  function handleChange(e) {
    Setinputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });

    Seterrors(
      validate({
        ...inputs,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleSubmit(e) {
    e.prevent.default();
    const aux = Object.keys(errors);
    if (aux.length === 0) {
      Setinputs = {
        email: "",
        password: "",
      };
      Seterrors = {
        email: "",
        password: "",
      };
      return alert("ok");
    }
    return alert("Error");
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <img
          className="imgbienvenida"
          src="https://www.pngplay.com/wp-content/uploads/10/Rick-And-Morty-Transparent-Background.png"
          alt="imagen bienvenida"
        />

        <label className="labelText">Email:</label>
        <input
          class="input-estilo"
          name="email"
          value={inputs.email}
          onChange={handleChange}
          placeholder="Escribe Tu Email"
        ></input>

        <p className="danger">{errors.email}</p>

        <label className="labelText">Password:</label>
        <input
          class="input-estilo"
          name="password"
          value={inputs.password}
          onChange={handleChange}
          placeholder="Escribe Tu Password"
        ></input>

        <p className="danger">{errors.password}</p>

        <Link to="/home">
          <button type="submit">Ingresar</button>
        </Link>
      </form>
    </div>
  );
}
