import React from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./styles/Login.css";

const regexEmail = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/;

const regexpPassword =
  /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{8,15}/;

export default function Login() {
  const [inputs, Setinputs] = useState({
    email: "",
    password: "",
  });

  const [errors, Seterrors] = useState({
    email: "",
    password: "",
  });

  function validate(inputs) {
    const errors = {};

    if (!inputs.email) {
      errors.email = (
        <div className="obligatoriE">The email field is required</div>
      );
    } else if (inputs.email.length > 35) {
      errors.email = (
        <div className="obligatoriE"> Cannot exceed 35 characters</div>
      );
    } else if (!regexEmail.test(inputs.email)) {
      errors.email = <div className="obligatoriE">invalid email </div>;
    } else if (!inputs.password) {
      errors.password = (
        <div className="obligatoriE">there must be a password</div>
      );
    } else if (!regexpPassword.test(inputs.password)) {
      errors.password = (
        <div className="obligatoriE">It must be a valid password</div>
      );
    }

    return errors;
  }

  function handleChange(e) {
    Setinputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });

    Seterrors(
      validate({
        ...inputs,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleSubmit(e) {
    e.prevent.default();
    const aux = Object.keys(errors);
    if (aux.length === 0) {
      Setinputs = {
        email: "",
        password: "",
      };
      Seterrors = {
        email: "",
        password: "",
      };
      return alert("ok");
    }
    return alert("Error");
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <img
          className="imgbienvenida"
          src="https://www.pngplay.com/wp-content/uploads/10/Rick-And-Morty-Transparent-Background.png"
          alt="imagen bienvenida"
        />
        <div className="margen">
          <label className="labelText">Email:</label>
          <input
            class="input-estilo"
            name="email"
            value={inputs.email}
            onChange={handleChange}
            placeholder="Write your Email"
          ></input>
        </div>
        <p className="danger">{errors.email}</p>

        <label className="labelText2">Password:</label>
        <input
          class="input-estilo"
          name="password"
          value={inputs.password}
          onChange={handleChange}
          placeholder="Write your Password"
        ></input>

        <p className="danger">{errors.password}</p>

        <Link to="/home">
          <button className="btnLog" type="submit">
            Ingresar
          </button>
        </Link>
      </form>
    </div>
  );
}
