import React, { useState, useEffect } from "react";
import "./App.css";
import Cards from "./components/Cards.jsx";
import NavBar from "./components/NavBar";
import axios from "axios";

function App() {
  const [characters, setCharacters] = useState([]);

  const handleAddRandomCharacter = () => {
    fetch("https://www.breakingbadapi.com/api/character/random")
      .then((response) => response.json())
      .then((data) => {
        // Aquí se puede agregar el código para agregar el personaje random a la lista de personajes existentes
      })
      .catch((error) => console.error(error));
  };

  function onSearch(id) {
    axios(`https://rickandmortyapi.com/api/character/${id}`).then(
      ({ data }) => {
        if (data.name) {
          let existe = characters.find((ch) => ch.id === data.id);
          if (existe) {
            alert("ya existe");
          } else {
            setCharacters((oldChars) => [...oldChars, data]);
          }
        } else {
          window.alert("¡No hay personajes con este ID!");
        }
      }
    );
  }

  function onClose(id) {
    setCharacters((oldChars) => {
      return oldChars.filter((ch) => ch.id !== id);
    });
  }

  return (
    <div className="App">
      <NavBar onSearch={onSearch} />
      <Cards
        onClose={onClose}
        handleAddRandomCharacter={handleAddRandomCharacter}
        characters={characters}
      />
    </div>
  );
}

export default App;
